import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from './components/Modal'; // Hazırladığımız Modal'ı çağırıyoruz

function App() {
  const [todoList, setTodoList] = useState([]);

  // Modal açık mı kapalı mı? (Başlangıçta kapalı: false)
  const [modal, setModal] = useState(false);

  // Düzenlenen veya yeni eklenen aktif görev
  const [activeItem, setActiveItem] = useState({
    title: "",
    description: "",
    completed: false,
  });

  useEffect(() => {
    refreshList();
  }, []);

  const refreshList = () => {
    axios
      .get("http://localhost:8000/api/todos/")
      .then((res) => setTodoList(res.data))
      .catch((err) => console.log(err));
  };

  // Modal'ı Aç/Kapa Fonksiyonu
  const toggle = () => {
    setModal(!modal);
  };

  // "Görev Ekle" butonuna basınca çalışır
  const createItem = () => {
    const item = { title: "", description: "", completed: false };
    setActiveItem(item); // Formu temizle
    setModal(true);      // Modalı aç
  };

  // Modal'daki "Kaydet" butonuna basınca çalışır
  const handleSubmit = (item) => {
    toggle(); // Modalı kapat

    // Backend'e POST isteği at (YENİ KAYIT)
    axios
      .post("http://localhost:8000/api/todos/", item)
      .then((res) => refreshList()) // Başarılıysa listeyi yenile
      .catch((err) => console.log(err));
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Yapılacaklar Listesi</h1>

      {/* Yeni Ekle Butonu */}
      <div className="mb-4">
        <button className="btn btn-primary" onClick={createItem}>
          + Yeni Görev Ekle
        </button>
      </div>

      <ul className="list-group">
        {todoList.map((item) => (
          <li 
            key={item.id} 
            className={`list-group-item d-flex justify-content-between align-items-center ${item.completed ? 'list-group-item-success' : ''}`}
          >
            <span>
              {item.completed ? <del>{item.title}</del> : item.title}
            </span>
            <span className="badge bg-primary rounded-pill">
                {item.completed ? "Tamamlandı" : "Yapılacak"}
            </span>
          </li>
        ))}
      </ul>

      {/* Eğer modal state'i true ise, Modal bileşenini ekrana bas */}
      {modal ? (
        <Modal
          activeItem={activeItem}
          toggle={toggle}
          onSave={handleSubmit}
        />
      ) : null}
    </div>
  );
}

export default App;
