import React, { useState, useEffect } from 'react'; // React'in özellikleri
import axios from 'axios'; // API ile konuşacak kütüphane

function App() {
  // 1. HAFIZA (State): Todo listesini tutacak boş bir kutu yapıyoruz.
  const [todoList, setTodoList] = useState([]);

  // 2. TETİKLEYİCİ (Effect): Sayfa açıldığında bu kod 1 kere çalışır.
  useEffect(() => {
    refreshList(); // Listeyi yenileme fonksiyonunu çağır
  }, []);

  // 3. İŞLEV (Function): Django'dan veriyi çeken fonksiyon
  const refreshList = () => {
    axios
      .get("http://localhost:8000/api/todos/") // Adrese git
      .then((res) => {
        setTodoList(res.data); // Gelen veriyi (res.data) hafızaya at
      })
      .catch((err) => console.log(err)); // Hata varsa konsola yaz
  };

  // 4. GÖRÜNTÜ (Render): Ekrana ne basılacak?
  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Yapılacaklar Listesi</h1>

      <ul className="list-group">
        {/* todoList içindeki her bir elemanı dön ve ekrana bas */}
        {todoList.map((item) => (
          <li 
            key={item.id} 
            className={`list-group-item d-flex justify-content-between align-items-center ${item.completed ? 'list-group-item-success' : ''}`}
          >
            <span>
              {/* Eğer tamamlandıysa üstünü çiz, değilse normal yaz */}
              {item.completed ? <del>{item.title}</del> : item.title}
            </span>

            <span className="badge bg-primary rounded-pill">
                {item.completed ? "Tamamlandı" : "Yapılacak"}
            </span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
